            "code": "\"use client\"\n\nimport { AnimatePresence } from \"motion/react\"\nimport * as motion from \"motion/react-client\"\nimport { useState } from \"react\"\n\nexport default function SharedLayoutAnimation() {\n    const [selectedTab, setSelectedTab] = useState(tabs[0])\n\n    return (\n        <div style={container}>\n            <nav style={nav}>\n                <ul style={tabsContainer}>\n                    {tabs.map((item) => (\n                        <motion.li\n                            key={item.label}\n                            initial={false}\n                            animate={{\n                                backgroundColor:\n                                    item === selectedTab ? \"#eee\" : \"#eee0\",\n                            }}\n                            style={tab}\n                            onClick={() => setSelectedTab(item)}\n                        >\n                            {`${item.icon} ${item.label}`}\n                            {item === selectedTab ? (\n                                <motion.div\n                                    style={underline}\n                                    layoutId=\"underline\"\n                                    id=\"underline\"\n                                />\n                            ) : null}\n                        </motion.li>\n                    ))}\n                </ul>\n            </nav>\n            <main style={iconContainer}>\n                <AnimatePresence mode=\"wait\">\n                    <motion.div\n                        key={selectedTab ? selectedTab.label : \"empty\"}\n                        initial={{ y: 10, opacity: 0 }}\n                        animate={{ y: 0, opacity: 1 }}\n                        exit={{ y: -10, opacity: 0 }}\n                        transition={{ duration: 0.2 }}\n                        style={icon}\n                    >\n                        {selectedTab ? selectedTab.icon : \"\"}\n                    </motion.div>\n                </AnimatePresence>\n            </main>\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst container: React.CSSProperties = {\n    width: 480,\n    height: \"60vh\",\n    maxHeight: 360,\n    borderRadius: 10,\n    background: \"white\",\n    overflow: \"hidden\",\n    boxShadow:\n        \"0 1px 1px hsl(0deg 0% 0% / 0.075), 0 2px 2px hsl(0deg 0% 0% / 0.075), 0 4px 4px hsl(0deg 0% 0% / 0.075), 0 8px 8px hsl(0deg 0% 0% / 0.075), 0 16px 16px hsl(0deg 0% 0% / 0.075), 0 2px 2px hsl(0deg 0% 0% / 0.075), 0 4px 4px hsl(0deg 0% 0% / 0.075), 0 8px 8px hsl(0deg 0% 0% / 0.075), 0 16px 16px hsl(0deg 0% 0% / 0.075)\",\n    display: \"flex\",\n    flexDirection: \"column\",\n}\n\nconst nav: React.CSSProperties = {\n    background: \"#fdfdfd\",\n    padding: \"5px 5px 0\",\n    borderRadius: \"10px\",\n    borderBottomLeftRadius: 0,\n    borderBottomRightRadius: 0,\n    borderBottom: \"1px solid #eeeeee\",\n    height: 44,\n}\n\nconst tabsStyles: React.CSSProperties = {\n    listStyle: \"none\",\n    padding: 0,\n    margin: 0,\n    fontWeight: 500,\n    fontSize: 14,\n}\n\nconst tabsContainer: React.CSSProperties = {\n    ...tabsStyles,\n    display: \"flex\",\n    width: \"100%\",\n}\n\nconst tab: React.CSSProperties = {\n    ...tabsStyles,\n    borderRadius: 5,\n    borderBottomLeftRadius: 0,\n    borderBottomRightRadius: 0,\n    width: \"100%\",\n    padding: \"10px 15px\",\n    position: \"relative\",\n    background: \"white\",\n    cursor: \"pointer\",\n    height: 24,\n    display: \"flex\",\n    justifyContent: \"space-between\",\n    alignItems: \"center\",\n    flex: 1,\n    minWidth: 0,\n    userSelect: \"none\",\n    color: \"#0f1115\",\n}\n\nconst underline: React.CSSProperties = {\n    position: \"absolute\",\n    bottom: -2,\n    left: 0,\n    right: 0,\n    height: 2,\n    background: \"var(--accent)\",\n}\n\nconst iconContainer: React.CSSProperties = {\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    flex: 1,\n}\n\nconst icon: React.CSSProperties = {\n    fontSize: 128,\n}\n\n/**\n * ==============   Data   ================\n */\n\nconst allIngredients = [\n    { icon: \"\", label: \"Tomato\" },\n    { icon: \"\", label: \"Lettuce\" },\n    { icon: \"\", label: \"Cheese\" },\n    { icon: \"\", label: \"Carrot\" },\n    { icon: \"\", label: \"Banana\" },\n    { icon: \"\", label: \"Blueberries\" },\n    { icon: \"\", label: \"Champers?\" },\n]\n\nconst [tomato, lettuce, cheese] = allIngredients\nconst tabs = [tomato, lettuce, cheese]\n"
