            "code": "\"use client\"\n\nimport { Ticker } from \"motion-plus/react\"\nimport { motion, useScroll, useTransform } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction Box({\n    src,\n    title,\n    setCurrentItem,\n}: {\n    src: string\n    title: string\n    setCurrentItem: (item: string) => void\n}) {\n    return (\n        <div className=\"item\">\n            <motion.img\n                src={`/photos/boxart/n64/${src}.jpg`}\n                alt={title + \" boxart\"}\n                draggable={false}\n                onViewportEnter={() => setCurrentItem(title)}\n                viewport={{ amount: 1 }}\n            />\n        </div>\n    )\n}\n\nexport default function TickerDraggable() {\n    const { scrollY } = useScroll()\n    const [currentItem, setCurrentItem] = useState(\"Mario Kart 64\")\n\n    const reverseOffset = useTransform(() => scrollY.get() * -1)\n\n    return (\n        <>\n            <Ticker\n                className=\"ticker\"\n                id=\"top-ticker\"\n                items={[<span>{currentItem}</span>]}\n                style={{ position: \"fixed\" }}\n                offset={scrollY}\n            />\n            <Ticker\n                className=\"ticker\"\n                id=\"bottom-ticker\"\n                items={[<span>{currentItem}</span>]}\n                style={{ position: \"fixed\" }}\n                offset={reverseOffset}\n            />\n            <Box\n                src=\"mariokart64\"\n                title=\"Mario Kart 64\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"goldeneye\"\n                title=\"GoldenEye 007\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"mario64\"\n                title=\"Super Mario 64\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"ocarinaoftime\"\n                title=\"Zelda: Ocarina of Time\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"smashbros\"\n                title=\"Super Smash Bros\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"1080\"\n                title=\"1080 Snowboarding\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"majorasmask\"\n                title=\"Majora's Mask\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"perfectdark\"\n                title=\"Perfect Dark\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Box\n                src=\"goemon\"\n                title=\"Mythical Ninja\"\n                setCurrentItem={setCurrentItem}\n            />\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        @import url('https://fonts.googleapis.com/css2?family=Barlow:wght@800&display=swap');\n        \n        #sandbox {\n          align-items: stretch;\n        }\n\n        .container {\n            display: flex;\n            flex-direction: row;\n            gap: 20px;\n            align-items: center;\n            justify-content: center;\n            overflow: hidden;\n        }\n\n        .item {\n            height: 100vh;\n            overflow: hidden;\n            position: relative;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n        }\n\n        .item img {\n            width: 270px;\n            height: 382.5px;\n            object-fit: cover;\n        }\n\n        .ticker {\n            position: fixed;\n            left: 0;\n            right: 0;\n        }\n\n        .ticker span {\n            color: var(--background);\n            -webkit-text-stroke: 1px #f5f5f5;\n            text-stroke: 1px #f5f5f5;\n            opacity: 0.5;\n            font-size: 72px;\n            font-family: \"Barlow\", sans-serif;\n            font-weight: 800;\n            font-style: normal;\n        }\n\n        #top-ticker {\n            top: 80px;\n        }\n\n        #bottom-ticker {\n            bottom: 80px;\n        }\n\n        @media (max-width: 768px) {\n            .item img {\n                width: 200px;\n                height: 285px;\n            }\n        }\n        \n      `}\n        </style>\n    )\n}\n"
