            "code": "\"use client\"\n\nimport { Ticker } from \"motion-plus/react\"\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction TickerLink({\n    href = \"#\",\n    children,\n}: {\n    href: string\n    children: string\n}) {\n    const [isHovered, setIsHovered] = useState(false)\n\n    return (\n        <motion.a\n            href={href}\n            onHoverStart={() => setIsHovered(true)}\n            onHoverEnd={() => setIsHovered(false)}\n        >\n            {children}\n            <AnimatePresence>\n                {isHovered && (\n                    <Ticker\n                        className=\"ticker\"\n                        items={[children, \"*\"]}\n                        style={{ position: \"absolute\" }}\n                        initial={{ clipPath: \"inset(50% 0 50% 0)\" }}\n                        animate={{ clipPath: \"inset(0% 0 0% 0)\" }}\n                        exit={{\n                            clipPath: \"inset(50% 0 50% 0)\",\n                            transition: { duration: 0.1 },\n                        }}\n                        transition={{\n                            duration: 0.2,\n                            ease: \"easeOut\",\n                        }}\n                    />\n                )}\n            </AnimatePresence>\n        </motion.a>\n    )\n}\n\nexport default function TickerTextHoverEffect() {\n    return (\n        <nav className=\"container\">\n            <div className=\"line\"></div>\n            <TickerLink href=\"#\">London</TickerLink>\n            <div className=\"line\"></div>\n            <TickerLink href=\"#\">Tokyo</TickerLink>\n            <div className=\"line\"></div>\n            <TickerLink href=\"#\">Amsterdam</TickerLink>\n            <div className=\"line\"></div>\n            <Stylesheet />\n        </nav>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        #sandbox {\n          align-items: stretch;\n        }\n\n        .container {\n            display: flex;\n            flex-direction: column;\n            align-items: center;\n            justify-content: center;\n            overflow: hidden;\n        }\n\n       .container a {\n            font-size: 48px;\n            text-transform: uppercase;\n            font-weight: bold;\n            color: #f5f5f5;\n            line-height: 1;\n            width: 100%;\n            display: flex;\n            justify-content: center;\n            align-items: center;\n            padding: 10px 0;\n            position: relative;\n        }\n\n        .ticker {\n            position: absolute;\n            top: 0;\n            left: 0;\n            bottom: 0;\n            right: 0;\n            overflow: hidden;\n            background: #f5f5f5;\n            color: #0f1115;\n            font-style: italic;\n        }\n\n        .line {\n            width: 100%;\n            height: 1px;\n            background-color: #f5f5f5;\n        }\n      `}\n        </style>\n    )\n}\n"
