            "category": "Ticker",
            "code": "\"use client\"\n\nimport { Ticker, useTickerItem } from \"motion-plus/react\"\nimport { motion, useTransform } from \"motion/react\"\n\nfunction Box({ src, title }: { src: string; title: string }) {\n    const { offset } = useTickerItem()\n    const offsetRange = [0, 600]\n    const { opacity, scale, filter } = useTransform(offset, offsetRange, {\n        opacity: [1, 0],\n        scale: [1, 0.6],\n        filter: [\"blur(0px)\", \"blur(10px)\"],\n    })\n\n    return (\n        <motion.div className=\"item\" style={{ opacity, filter, scale }}>\n            <motion.img\n                src={`/photos/boxart/${src}.png`}\n                alt={title + \" boxart\"}\n                style={{ width: \"100%\", height: \"100%\" }}\n            />\n        </motion.div>\n    )\n}\n\nexport default function TickerExample() {\n    return (\n        <>\n            <Ticker\n                className=\"ticker\"\n                hoverFactor={0}\n                items={[\n                    <Box src=\"sonic3\" title=\"Sonic 3\" />,\n                    <Box src=\"shinobi\" title=\"Shinobi\" />,\n                    <Box src=\"sonic1\" title=\"Sonic 1\" />,\n                    <Box src=\"streetsofrage2\" title=\"Street Fighter 2\" />,\n                ]}\n                overflow\n            />\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        #sandbox {\n          align-items: center;\n          overflow: hidden;\n        }\n\n        .ticker {\n            max-width: 500px;   \n        }\n\n        .item {\n            width: 180px;\n            height: 255px;\n            overflow: hidden;\n            position: relative;\n        }\n      `}\n        </style>\n    )\n}\n"
