            "code": "\"use client\"\n\nimport { Ticker } from \"motion-plus/react\"\n\nfunction Box({ src, title }: { src: string; title: string }) {\n    return (\n        <div className=\"item\">\n            <img\n                src={`/photos/boxart/n64/${src}.jpg`}\n                alt={title + \" boxart\"}\n                draggable={false}\n            />\n        </div>\n    )\n}\n\nexport default function TickerYAxis() {\n    return (\n        <div className=\"container\">\n            <Ticker\n                axis=\"y\"\n                hoverFactor={0.5}\n                items={[\n                    <Box src=\"mariokart64\" title=\"Mario Kart 64\" />,\n                    <Box src=\"goldeneye\" title=\"GoldenEye 007\" />,\n                    <Box src=\"mario64\" title=\"Super Mario 64\" />,\n                ]}\n                className=\"ticker\"\n            />\n            <Ticker\n                axis=\"y\"\n                hoverFactor={0.5}\n                velocity={-50}\n                items={[\n                    <Box src=\"majorasmask\" title=\"Majora's Mask\" />,\n                    <Box src=\"perfectdark\" title=\"Perfect Dark\" />,\n                    <Box src=\"goemon\" title=\"Mythical Ninja\" />,\n                ]}\n                className=\"ticker\"\n            />\n            <Ticker\n                axis=\"y\"\n                hoverFactor={0.5}\n                velocity={70}\n                items={[\n                    <Box src=\"ocarinaoftime\" title=\"Zelda: Ocarina of Time\" />,\n                    <Box src=\"smashbros\" title=\"Super Smash Bros\" />,\n                    <Box src=\"1080\" title=\"1080 Snowboarding\" />,\n                ]}\n                className=\"ticker\"\n            />\n            <Stylesheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        body {\n            overflow: hidden;\n        }\n\n        .container {\n            display: flex;\n            flex-direction: row;\n            gap: 20px;\n            align-items: center;\n            justify-content: center;\n            overflow: hidden;\n            height: 60vh;\n            transform: perspective(700px) rotateX(20deg)\n        }\n\n        .item {\n            width: 180px;\n            height: 255px;\n            overflow: hidden;\n            position: relative;\n        }\n\n        .item img {\n            width: 100%;\n            height: 100%;\n            object-fit: cover;\n        }\n\n        .ticker {\n            mask-image: linear-gradient(to bottom, transparent 0%, black 20%, black 80%, transparent 100%);\n            touch-action: none;\n            user-select: none;\n            max-height: 100%;\n        }\n      `}\n        </style>\n    )\n}\n"
