            "category": "Interactions",
            "code": "\"use client\"\n\nimport { motion, useSpring } from \"motion/react\"\nimport { useRef } from \"react\"\n\nexport default function TiltCard({ maxTilt = 15 }: { maxTilt?: number }) {\n    const cardRef = useRef<HTMLDivElement>(null)\n    const z = useSpring(0)\n    const rotateX = useSpring(0)\n    const rotateY = useSpring(0)\n\n    const calculateTilt = (event: React.PointerEvent<HTMLDivElement>) => {\n        if (!cardRef.current) return { rotateX: 0, rotateY: 0 }\n\n        const rect = cardRef.current.getBoundingClientRect()\n        const x = event.clientX - rect.left\n        const y = event.clientY - rect.top\n\n        // Convert coordinates to percentages\n        const xPercent = x / rect.width\n        const yPercent = y / rect.height\n\n        return {\n            rotateX: maxTilt * (0.5 - yPercent),\n            rotateY: maxTilt * (xPercent - 0.5),\n        }\n    }\n\n    return (\n        <motion.div\n            ref={cardRef}\n            style={{\n                ...styles.card,\n                z,\n                rotateX,\n                rotateY,\n            }}\n            transition={{ type: \"spring\", stiffness: 200, damping: 20 }}\n            onPointerMove={(e) => {\n                const tilt = calculateTilt(e)\n                rotateX.set(tilt.rotateX)\n                rotateY.set(tilt.rotateY)\n            }}\n            onPointerLeave={() => {\n                rotateX.set(0)\n                rotateY.set(0)\n                z.set(0)\n            }}\n            onPointerEnter={() => {\n                z.set(-10)\n            }}\n        >\n            <img\n                src=\"/photos/amsterdam-cityscape/1.jpg\"\n                alt=\"Amsterdam cityscape\"\n                style={styles.image}\n            />\n            <div style={styles.gradientOverlay} />\n            <div style={styles.caption}>A high-rise in Amsterdam Zuid</div>\n        </motion.div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst styles = {\n    card: {\n        position: \"relative\",\n        width: 300,\n        height: 400,\n        borderRadius: 20,\n        overflow: \"hidden\",\n        background: \"#0b1011\",\n        border: \"1px solid #1d2628\",\n        willChange: \"transform\",\n        transformPerspective: 500,\n    },\n    image: {\n        width: \"100%\",\n        height: \"100%\",\n        objectFit: \"cover\",\n        WebkitTouchCallout: \"none\",\n        WebkitUserSelect: \"none\",\n        userSelect: \"none\",\n        touchAction: \"none\",\n    },\n    gradientOverlay: {\n        position: \"absolute\",\n        bottom: 0,\n        left: 0,\n        right: 0,\n        height: 120,\n        zIndex: 1,\n        background:\n            \"linear-gradient(to top, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0))\",\n    },\n    caption: {\n        position: \"absolute\",\n        zIndex: 2,\n        bottom: 20,\n        left: 20,\n        right: 20,\n        color: \"#f5f5f5\",\n        fontSize: 16,\n        lineHeight: 1.5,\n        textShadow: \"0 1px 2px rgba(0, 0, 0, 0.5)\",\n    },\n} as const\n"
