            "category": "Lists",
            "code": "\"use client\"\n\nimport { animate, motion, Reorder, useMotionValue } from \"motion/react\"\nimport { useRef, useState } from \"react\"\n\ninterface TodoItem {\n    id: number\n    text: string\n    completed: boolean\n    color: string\n}\n\nexport default function TodoList() {\n    const { todos, setTodos, toggleTodo } = useTodo(initialTodos)\n\n    return (\n        <div className=\"todo-container\">\n            <Reorder.Group\n                axis=\"y\"\n                values={todos}\n                onReorder={setTodos}\n                className=\"todo-list\"\n            >\n                {todos.map((todo) => (\n                    <Item\n                        key={todo.id}\n                        todo={todo}\n                        onToggle={() => toggleTodo(todo.id)}\n                    />\n                ))}\n            </Reorder.Group>\n            <StyleSheet />\n        </div>\n    )\n}\n\ninterface ItemProps {\n    todo: TodoItem\n    onToggle: () => void\n}\n\nfunction Item({ todo, onToggle }: ItemProps) {\n    const boxShadow = useMotionValue(\"0 1px 2px rgba(0,0,0,0.1)\")\n    const checkboxRef = useRef<HTMLButtonElement>(null)\n\n    return (\n        <Reorder.Item\n            value={todo}\n            id={String(todo.id)}\n            className=\"todo-item\"\n            style={{ boxShadow }}\n            onDragStart={() => {\n                animate(boxShadow, \"0 7px 24px rgba(0,0,0,0.2)\")\n            }}\n            onDragEnd={() => {\n                animate(boxShadow, \"0 1px 2px rgba(0,0,0,0.1)\")\n            }}\n        >\n            <button\n                ref={checkboxRef}\n                className=\"todo-checkbox\"\n                style={{\n                    borderColor: todo.color,\n                    backgroundColor: todo.completed\n                        ? todo.color\n                        : \"transparent\",\n                }}\n                onClick={(e) => {\n                    e.stopPropagation()\n                    onToggle()\n                }}\n                onPointerDownCapture={(e) => e.stopPropagation()}\n            >\n                {todo.completed && (\n                    <svg\n                        width=\"10\"\n                        height=\"8\"\n                        viewBox=\"0 0 12 10\"\n                        fill=\"none\"\n                        style={{ display: \"block\" }}\n                    >\n                        <path\n                            d=\"M1 5L4.5 8.5L11 1\"\n                            stroke=\"white\"\n                            strokeWidth=\"2.5\"\n                            strokeLinecap=\"round\"\n                            strokeLinejoin=\"round\"\n                        />\n                    </svg>\n                )}\n            </button>\n            <span className=\"todo-text-wrapper\">\n                <motion.span\n                    animate={{ opacity: todo.completed ? 0.45 : 1 }}\n                    transition={{ duration: 0.4 }}\n                    className=\"todo-text\"\n                >\n                    {todo.text}\n                </motion.span>\n                <motion.span\n                    initial={{ scaleX: 0 }}\n                    animate={{ scaleX: todo.completed ? 1 : 0 }}\n                    transition={{ duration: 0.4, ease: \"easeOut\" }}\n                    className=\"todo-strikethrough\"\n                    style={{ backgroundColor: todo.color }}\n                />\n            </span>\n        </Reorder.Item>\n    )\n}\n\n/**\n * ==============   Hooks   ================\n */\n\n// Handle state and delayed reorder when an item is completed\nfunction useTodo(initial: TodoItem[]) {\n    const [todos, setTodos] = useState(initial)\n\n    const toggleTodo = (id: number) => {\n        setTodos((prev) => {\n            const updated = prev.map((todo) =>\n                todo.id === id ? { ...todo, completed: !todo.completed } : todo\n            )\n\n            const targetTodo = updated.find((t) => t.id === id)\n            if (targetTodo?.completed) {\n                setTimeout(() => {\n                    setTodos((current) => {\n                        const completed = current.filter((t) => t.completed)\n                        const uncompleted = current.filter((t) => !t.completed)\n                        return [...uncompleted, ...completed]\n                    })\n                }, 600)\n            }\n\n            return updated\n        })\n    }\n\n    return { todos, setTodos, toggleTodo }\n}\n\n/**\n * ==============   Mock Data   ================\n */\nconst COLORS = [\n    \"#ff0088\",\n    \"#dd00ee\",\n    \"#9911ff\",\n    \"#1e75f7\",\n    \"#0cdcf7\",\n    \"#8df0cc\",\n]\n\nconst initialTodos: TodoItem[] = [\n    {\n        id: 0,\n        text: \"Review project proposal\",\n        completed: false,\n        color: COLORS[0],\n    },\n    { id: 1, text: \"Update documentation\", completed: false, color: COLORS[1] },\n    {\n        id: 2,\n        text: \"Schedule team meeting\",\n        completed: false,\n        color: COLORS[2],\n    },\n    { id: 3, text: \"Test new features\", completed: false, color: COLORS[3] },\n    { id: 4, text: \"Deploy to staging\", completed: false, color: COLORS[4] },\n    { id: 5, text: \"Send weekly report\", completed: false, color: COLORS[5] },\n    {\n        id: 6,\n        text: \"Prepare presentation slides\",\n        completed: false,\n        color: COLORS[0],\n    },\n    { id: 7, text: \"Review pull requests\", completed: false, color: COLORS[1] },\n]\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .todo-container {\n                width: 100%;\n                max-width: 340px;\n                height: 240px;\n                overflow: auto;\n                padding: 16px;\n                background-color: #ffffff;\n                border-radius: 16px;\n                border: 1px solid #e5e7eb;\n                box-shadow: 0 4px 24px rgba(0,0,0,0.08);\n                scrollbar-width: thin;\n                scrollbar-color: #d1d5db transparent;\n            }\n        \n            .todo-container::-webkit-scrollbar {\n                width: 6px;\n            }\n\n            .todo-container::-webkit-scrollbar-track {\n                background: transparent;\n            }\n\n            .todo-container::-webkit-scrollbar-thumb {\n                background: #d1d5db;\n                border-radius: 3px;\n            }\n\n            .todo-list {\n                list-style: none;\n                padding: 0;\n                margin: 0;\n                display: flex;\n                flex-direction: column;\n                gap: 10px;\n            }\n\n            .todo-item {\n                display: flex;\n                align-items: center;\n                gap: 12px;\n                padding: 12px 14px;\n                background-color: #f9fafb;\n                border-radius: 10px;\n                cursor: grab;\n            }\n\n            .todo-checkbox {\n                width: 20px;\n                height: 20px;\n                border-radius: 6px;\n                border: 2px solid;\n                display: flex;\n                align-items: center;\n                justify-content: center;\n                cursor: pointer;\n                flex-shrink: 0;\n                transition: background-color 0.2s;\n            }\n\n            .todo-text-wrapper {\n                position: relative;\n                display: inline;\n            }\n\n            .todo-text {\n                font-size: 14px;\n                font-weight: 500;\n                color: #374151;\n            }\n\n            .todo-strikethrough {\n                position: absolute;\n                left: 0;\n                right: 0;\n                top: 50%;\n                height: 1.5px;\n                transform-origin: left center;\n                border-radius: 1px;\n                pointer-events: none;\n            }\n        `}</style>\n    )\n}\n"
