            "category": "Text",
            "code": "\"use client\"\n\nimport { animate, AnimationSequence, mix, wrap } from \"motion\"\nimport { splitText } from \"motion-plus\"\nimport { Typewriter } from \"motion-plus/react\"\nimport { useEffect, useRef, useState } from \"react\"\n\nexport default function TypewriterExplodeExample({\n    text = [\n        \"Ten\",\n        \"Nine\",\n        \"Eight\",\n        \"Seven\",\n        \"Six\",\n        \"Five\",\n        \"Four\",\n        \"Three\",\n        \"Two\",\n        \"One\",\n    ],\n}: {\n    text?: string[]\n}) {\n    const [index, setIndex] = useState(0)\n    const [state, setState] = useState<\"typing\" | \"exploding\">(\"typing\")\n\n    return (\n        <>\n            <h2 className=\"container\">\n                {state === \"typing\" ? (\n                    <Typewriter\n                        as=\"div\"\n                        cursorClassName=\"cursor\"\n                        onComplete={() => {\n                            setState(\"exploding\")\n                        }}\n                        className=\"animating-text\"\n                        cursorStyle={{ height: \"0.8em\", top: \"0.05em\" }}\n                    >\n                        {text[index]}\n                    </Typewriter>\n                ) : (\n                    <ExplodingText\n                        text={text[index]}\n                        onComplete={() => {\n                            setIndex(wrap(0, text.length, index + 1))\n                            setState(\"typing\")\n                        }}\n                    />\n                )}\n            </h2>\n            <Stylesheet />\n        </>\n    )\n}\n\nfunction ExplodingText({\n    text,\n    minVelocity = 200,\n    maxVelocity = 400,\n    onComplete,\n}: {\n    text: string\n    minVelocity?: number\n    maxVelocity?: number\n    onComplete: () => void\n}) {\n    const ref = useRef<HTMLDivElement>(null)\n\n    useEffect(() => {\n        if (!ref.current) return\n\n        const { chars } = splitText(ref.current)\n\n        const sequence: AnimationSequence = chars.map((char) => {\n            const velocity = mix(minVelocity, maxVelocity, Math.random())\n            // Limit angle to forward (right) range: -45deg to +45deg (in radians)\n            const angle = (Math.random() - 0.5) * (Math.PI / 2)\n\n            return [\n                char,\n                {\n                    opacity: 0,\n                    x: Math.cos(angle) * velocity,\n                    y: Math.sin(angle) * velocity,\n                },\n                {\n                    type: \"inertia\",\n                    velocity,\n                    at: \"<\",\n                },\n            ]\n        })\n\n        const animation = animate(sequence, { delay: 0.1 })\n        animation.finished.then(onComplete)\n\n        return () => {\n            animation.stop()\n        }\n    }, [])\n\n    return (\n        <div className=\"animating-text\" ref={ref} aria-label={text}>\n            {text}\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n            body {\n                overflow: hidden;\n            }\n            \n            .container {\n                display: flex;\n                align-items: flex-start;\n                gap: 0;\n                flex-direction: column;\n                width: 400px;\n                font-size: 48px;\n                text-transform: uppercase;\n                line-height: 1;\n                color: var(--accent);\n                height: 48px;\n            }\n\n            .animating-text span,\n            .animating-text div {\n                font-size: 48px;\n                font-weight: 700;\n                line-height: 1;\n                height: 48px;\n            }\n\n            .cursor {\n                background: #ff0088;\n                width: 3px;\n            }\n        `}\n        </style>\n    )\n}\n"
