            "category": "Text",
            "code": "\"use client\"\n\nimport { delay, wrap } from \"motion\"\nimport { Typewriter } from \"motion-plus/react\"\nimport { useState } from \"react\"\n\ninterface ScriptSegment {\n    text: string\n    endDelay?: number\n    backspace?: \"character\" | \"word\" | \"all\"\n    backspaceFactor?: number\n}\n\nexport default function TypewriterNaturalTypingExample({\n    script = [\n        { text: \"Hello everyone!\", endDelay: 0.8 },\n        {\n            text: \"Hello everyone! This is the new Typewriter component in Motion for React.\",\n        },\n        {\n            text: \"It uses a natural typing animation that varies the timing of each stroke based on the cursor position within a word, overall word length, punctuation, sentence boundaries, and more!\",\n            endDelay: 1,\n            backspace: \"character\",\n        },\n        {\n            text: \"When the content changes, the text can be bcksapced\",\n\n            endDelay: 0.3,\n        },\n        {\n            text: \"When the content changes, the text can be backspaced away.\",\n            backspace: \"character\",\n        },\n        { text: \"Backspace by character...\" },\n        { text: \"Backspace by word...\", backspace: \"character\" },\n        {\n            text: \"Or backspace all at once!\",\n            backspace: \"word\",\n            backspaceFactor: 6,\n        },\n        {\n            text: \"There's more little big details, too.\",\n            backspace: \"all\",\n        },\n        {\n            text: \"There's more little big details, too. For example, the cursor only flashes when typing is paused - just like real life.\",\n            backspace: \"all\",\n            endDelay: 2,\n        },\n        {\n            text: \"It's easy to use and can be customized to fit your needs. Styling, animations - everything!\",\n        },\n        {\n            text: \"Oh yeah... It's tiny, just +1.3kb. And it's Motion+ exclusive!\",\n            endDelay: 2,\n        },\n    ],\n}: {\n    script?: ScriptSegment[]\n}) {\n    const [paragraphIndex, setParagraphIndex] = useState(0)\n\n    const {\n        text,\n        endDelay = 0.8,\n        backspace = \"word\",\n        backspaceFactor = 0.5,\n    } = script[paragraphIndex]\n\n    return (\n        <div style={container}>\n            <Typewriter\n                as=\"p\"\n                speed=\"fast\"\n                cursorStyle={cursor}\n                textStyle={monospace}\n                backspace={backspace}\n                backspaceFactor={backspaceFactor}\n                onComplete={() => {\n                    delay(() => {\n                        setParagraphIndex(\n                            wrap(0, script.length, paragraphIndex + 1)\n                        )\n                    }, endDelay)\n                }}\n            >\n                {text}\n            </Typewriter>\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nconst monospace: React.CSSProperties = {\n    fontFamily: `\"Azeret Mono\", monospace`,\n    fontSize: 16,\n    lineHeight: \"20px\",\n    color: \"var(--accent)\",\n    textAlign: \"left\",\n}\n\nconst container: any = {\n    \"--border\": \"rgba(0, 0, 0, 0.1)\",\n    \"--line-height\": \"21px\",\n    display: \"flex\",\n    alignItems: \"flex-start\",\n    gap: 0,\n    flexDirection: \"column\",\n    maxWidth: 400,\n    width: \"100%\",\n    height: 120,\n    backgroundImage:\n        \"repeating-linear-gradient(transparent 0px, transparent calc(var(--line-height) - 1px), #1d2628 calc(var(--line-height) - 1px), #1d2628 var(--line-height))\",\n    padding: \"24px 16px\",\n    position: \"relative\",\n}\n\nconst cursor: React.CSSProperties = {\n    background: \"#0d63f8\",\n    width: 2,\n}\n"
