            "category": "Basics",
            "code": "\"use client\"\n\nimport { AnimatePresence, motion, usePresenceData, wrap } from \"motion/react\"\nimport { forwardRef, SVGProps, useState } from \"react\"\n\nexport default function UsePresenceData() {\n    const items = [1, 2, 3, 4, 5, 6]\n    const [selectedItem, setSelectedItem] = useState(items[0])\n    const [direction, setDirection] = useState<1 | -1>(1)\n\n    function setSlide(newDirection: 1 | -1) {\n        const nextItem = wrap(1, items.length, selectedItem + newDirection)\n        setSelectedItem(nextItem)\n        setDirection(newDirection)\n    }\n\n    const color = `var(--hue-${selectedItem})`\n\n    return (\n        <div style={container}>\n            <motion.button\n                initial={false}\n                animate={{ backgroundColor: color }}\n                aria-label=\"Previous\"\n                style={button}\n                onClick={() => setSlide(-1)}\n                whileFocus={{ outline: `2px solid ${color}` }}\n                whileTap={{ scale: 0.9 }}\n            >\n                <ArrowLeft />\n            </motion.button>\n            <AnimatePresence\n                custom={direction}\n                initial={false}\n                mode=\"popLayout\"\n            >\n                <Slide key={selectedItem} color={color} />\n            </AnimatePresence>\n            <motion.button\n                initial={false}\n                animate={{ backgroundColor: color }}\n                aria-label=\"Next\"\n                style={button}\n                onClick={() => setSlide(1)}\n                whileFocus={{ outline: `2px solid ${color}` }}\n                whileTap={{ scale: 0.9 }}\n            >\n                <ArrowRight />\n            </motion.button>\n        </div>\n    )\n}\n\nconst Slide = forwardRef(function Slide(\n    { color }: { color: string },\n    ref: React.Ref<HTMLDivElement>\n) {\n    const direction = usePresenceData()\n    return (\n        <motion.div\n            ref={ref}\n            initial={{ opacity: 0, x: direction * 50 }}\n            animate={{\n                opacity: 1,\n                x: 0,\n                transition: {\n                    delay: 0.2,\n                    type: \"spring\",\n                    visualDuration: 0.3,\n                    bounce: 0.4,\n                },\n            }}\n            exit={{ opacity: 0, x: direction * -50 }}\n            style={{ ...box, backgroundColor: color }}\n        />\n    )\n})\n\n/**\n * ==============   Icons   ================\n */\nconst iconsProps: SVGProps<SVGSVGElement> = {\n    xmlns: \"http://www.w3.org/2000/svg\",\n    width: \"24\",\n    height: \"24\",\n    viewBox: \"0 0 24 24\",\n    fill: \"none\",\n    stroke: \"currentColor\",\n    strokeWidth: \"2\",\n    strokeLinecap: \"round\",\n    strokeLinejoin: \"round\",\n}\n\nfunction ArrowLeft() {\n    return (\n        <svg {...iconsProps}>\n            <path d=\"m12 19-7-7 7-7\" />\n            <path d=\"M19 12H5\" />\n        </svg>\n    )\n}\n\nfunction ArrowRight() {\n    return (\n        <svg {...iconsProps}>\n            <path d=\"M5 12h14\" />\n            <path d=\"m12 5 7 7-7 7\" />\n        </svg>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst container: React.CSSProperties = {\n    display: \"flex\",\n    position: \"relative\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    gap: 10,\n}\n\nconst box: React.CSSProperties = {\n    width: 150,\n    height: 150,\n    backgroundColor: \"#0cdcf7\",\n    borderRadius: \"10px\",\n}\n\nconst button: React.CSSProperties = {\n    backgroundColor: \"#0cdcf7\",\n    width: 40,\n    height: 40,\n    borderRadius: \"50%\",\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    position: \"relative\",\n    zIndex: 1,\n    outlineOffset: 2,\n}\n"
